import React from "react";
import { useState, useEffect } from "react";

import CardRepository from "../components/CardRepository/CardRepository";
import { useFetch } from "../hooks/useFetch";

const urlRepositories = "https://api.github.com/users/gabrielnicodemos/repos";
// const urlProfile = "https://api.github.com/users/gabrielnicodemos";

const Home = () => {
  const [search, setSearch] = useState("");
  const { data: items } = useFetch(urlRepositories);
  const [ repositories, setRepositories ] = ([items])
  // const { data: user } = useFetch(urlProfile);

  useEffect(() => {
    // const fetchData = async () => {
    //     setLoading(true);
    //     try {
    //         const res = await fetch(url);
    //         const data = await res.json();
    //         setData(data);
    //     } catch (error) {
    //         setError("Erro ao carregar os dados!")
    //     }
    //     setLoading(false);
    // }
    // fetchData();
    if (search.length > 0) {
      console.log("ENTROU", search);
      let searchRepository = repositories.filter((repository) => repository.name.includes(search));
      // setRepositories([searchRepository])
    }
  }, [search, repositories, setRepositories]);

  return (
    <div>
      <input
        type="text"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />
      {repositories &&
        repositories.map((repository) => (
          <CardRepository key={repository.id} repository={repository} />
        ))}
    </div>
  );
};

export default Home;
