import React from "react";
import { useState } from "react";

import CardRepository from "../components/CardRepository/CardRepository";
import { useFetch } from "../hooks/useFetch";

const urlRepositories = "https://api.github.com/users/gabrielnicodemos/repos";
// const urlProfile = "https://api.github.com/users/gabrielnicodemos";

const Home = () => {
  const [search, set] = useState("");
  const { data: repositories } = useFetch(urlRepositories);
  const [repositoriesSearch, setRepositoriesSearch] = useState([]);
  // const { data: user } = useFetch(urlProfile);

  const handleChange = ({ target }) => {
    if (target.value) {
      setRepositoriesSearch(repositories);
      return
    }

    const searchRepository = repositories.filter((r) => r.name.includes(target.value))
    setRepositoriesSearch(searchRepository)
    console.log(target.value);
    console.log(repositoriesSearch);
  };

  return (
    <div>
      <input type="text" value={repositoriesSearch} onChange={handleChange} />
      {repositories &&
        repositories.map((repository) => (
          <CardRepository key={repository.id} repository={repository} />
        ))}
    </div>
  );
};

export default Home;
