import React from "react";
import { useState, useEffect } from "react";

import CardRepository from "../components/CardRepository/CardRepository";
import { useFetch } from "../hooks/useFetch";

const urlRepositories = "https://api.github.com/users/gabrielnicodemos/repos";
// const urlProfile = "https://api.github.com/users/gabrielnicodemos";

const Home = () => {
  const [search, setSearch] = useState("");
  // const [filter, setFilter] = useState("");
  const [repositories, setRepositories] = useState([]);
  const { data: items } = useFetch(urlRepositories);

  useEffect(() => {
    if (items) {
      if (search.length > 0) {
        const repositoySearh = items.filter((repository) =>
          repository.name.toUpperCase().includes(search.toUpperCase())
        );
        setRepositories(repositoySearh);
      } else {
        setRepositories(items);
      }
    }
  }, [items, search]);

  const handleOrderDate = () => {
    const sortedDates = repositories
      .map((obj) => {
        return { ...obj, created_at: new Date(obj.created_at) };
      })
      .sort((a, b) => b.created_at - a.created_at);
    setRepositories(sortedDates);
  };

  const handleOrderAlfa = () => {
    const sortedName = [...repositories].sort((a, b) =>
      a.name.toUpperCase() > b.name.toUpperCase() ? 1 : -1
    );

    setRepositories(sortedName);
  };

  const handleFilter = () => {
    let filterChecknox = document.getElementsByName('optionsFilter');
    for (var i = 0; i < pacote.length; i++){
        if ( filterChecknox[i].checked ) {
            if(filterChecknox[i].value === "arq"){
                var Pacote_i = "Pacote UM";
                alert(Pacote_i);
            }else if (pacote[i].value === "public") {
                var Pacote_ii = "Pacote DOIS";
                alert(Pacote_ii);
            }else if (pacote[i].value === "private") {
                var Pacote_iii = "Pacote TRÊS";
                alert(Pacote_iii);
            }
        }
    }

  };

  return (
    <div>
      <input
        type="text"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />
      <button onClick={handleOrderDate}>Ordenar por data</button>
      <button onClick={handleOrderAlfa}>Ordem Alfabetica</button>
      <div>
        <div>
          <p>Arquivados:</p><input type="checkbox" name="optionsFilter" value="arq" onClick={handleFilter} />
        </div>
        <div>
          <p>Públicos:</p><input type="checkbox" name="optionsFilter" value="public" onClick={handleFilter} />
        </div>
        <div>
          <p>Privados:</p><input type="checkbox" name="optionsFilter" value="private" onClick={handleFilter} />
        </div>
      </div>
      {repositories &&
        repositories.map((repository) => (
          <CardRepository key={repository.id} repository={repository} />
        ))}
    </div>
  );
};

export default Home;
